import React from 'react';
import { getInformation ,getSupplier ,getActivity} from './helper/adminapicall';
import Base from '../core/Base';
import Popup from "reactjs-popup";

import { Link } from 'react-router-dom';

export default class ProductionDash extends React.Component {
    constructor(props) {
        super(props);
        this.state={
          information:[],
          supplier:[],
          activity:[],
          name:"",
          description:"",
          infoerror:false,
          supplyerror:false,
          activityerror:false
      }
    }
    componentDidMount()
    {

      const productionId = this.props.match.params.productionId;
      const tokrole=JSON.parse(localStorage.getItem("jwt"));
      const token=tokrole.access_token;

      getInformation(token,productionId).then(data=>{

        if(data.error){
          this.setState({infoerror:data.error,});

        }
        else{
          this.setState({information:data.informations});


        }
     }).catch(err=>{console.log(err)});


     getSupplier(token,productionId).then(data=>{
         if(data.error){
          this.setState({supplyerror:data.error});

         }
         else{
          this.setState({supplier:data.suppliers});

         }


     }).catch(err=>{console.log(err)})

      getActivity(token,productionId).then(data=>{
          if(data.error){
            this.setState({activityerror:data.error});

          }
          else{
            this.setState({activity:data.activities});

          }
      }).catch(err=>{console.log(err)})



    }

     info=()=>{
        return(
          (this.state.information.length)?
          (this.state.information.map((info, index) => {
            return (

          <h5  key={index} className="list-group-item mr-5">
            {info.name} : {info.description}

            </h5>
            )}))    :   (
          <li className="list-group-item">
          No Informations
          </li>
        )
        )
            }
         sup=()=>{
            return(
              (this.state.supplier.length)?
              (this.state.supplier.map((data, index) => {
                return (

              <h5  key={index} className="list-group-item mr-5">
                {data.name} : {data.description}

                </h5>
                )}))    :   (
              <li className="list-group-item">
              No Suppliers
              </li>
            )
            )

      } 

      act=()=>{
        return(
          (this.state.activity.length)?
          (this.state.activity.map((data, index) => {
            return (

          <h5  key={index} className="list-group-item mr-5">
            {data.type} : {data.name}

            </h5>
            )}))    :   (
          <li className="list-group-item">
          No Activity
          </li>
        )
        )

  }

  handleChange = name => event => {
    this.setState({[name]: event.target.value });
  };

  ButInfo=()=>{


    return(
      <Popup trigger={ <button className="btn btn-sm btn-success ml-5">Add</button>}
      modal
      position="center center"
      closeOnDocumentClick>
    <div className="container-fluid">
    <form>
    <div className="form-group">
      <p className="text-dark">Enter Information</p>
      <input
        type="text"
        className="form-control my-3"
        onChange={this.handleChange("name")}
        value={this.state.name}
        autoFocus
        required
        placeholder=""
      />
      console.log(this.state.name)
      <p className="text-dark">Enter Description</p>
      <input
        type="text"
        className="form-control my-3"
        onChange="description"
        value="ddd"
        autoFocus
        required
        placeholder=""
      />
      <button className="btn btn-info">
          Add information
        </button>
      </div>
      </form>
    </div>
    </Popup>

    )}   






    render() {


        return (
            <Base
                  title=""
                  description=""
                 className="container bg-info p-4">
                 <div className="row mb-5"> 
                 <div className="col-12">
                 <div className="card mb-4">
                 <h4 className="card-header bg-dark text-white text-center">Information Flow 
                  {this.ButInfo(this.props.match.params.productionId)}
                  </h4>

                  <ul className="list-group">
                  {this.info()}
                  </ul>
                  </div>
                    </div>
                    </div>

                    <div className="row mb-5"> 
                    <div className="col-12">
                    <div className="card mb-4">
                    <h4 className="card-header bg-dark text-white text-center">Server Flow 

                     <Link className="btn btn-sm btn-success ml-5">Add</Link>
                     </h4>

                     <ul className="list-group">

                     {this.sup()}
                     </ul>

                     </div>
                     </div>
                     </div>

                     <div className="row mb-5"> 
                     <div className="col-12">
                     <div className="card mb-4">
                     <h4 className="card-header bg-dark text-white text-center">Activity Flow 

                      <Link className="btn btn-sm btn-success ml-5">Add</Link>
                      </h4>

                      <ul className="list-group">
                      {this.act()}
                      </ul>

                      </div>
                      </div>
                      </div>








                  </Base>
        );
    }
}
