import React from "react";
import {
  getInformation,
  getSupplier,
  getActivity,
} from "./helper/adminapicall";
import Base from "../core/Base";
import Popup from "reactjs-popup";
import { NotificationContainer, NotificationManager } from 'react-notifications';
import { Link } from "react-router-dom";

export default class ProductionDash extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      information: [],
      supplier: [],
      activity: [],
      name: "",
      name1:"",
      description: "",
      description:"",
      infoerror: false,
      supplyerror: false,
      activityerror: false,
    };
  }
  componentDidMount() {
    const productionId = this.props.match.params.productionId;
    const tokrole = JSON.parse(localStorage.getItem("jwt"));
    const token = tokrole.access_token;

    getInformation(token, productionId)
      .then((data) => {
        if (data.error) {
          this.setState({ infoerror: data.error });
        } else {
          this.setState({ information: data.informations });
        }
      })
      .catch((err) => {
        console.log(err);
      });

    getSupplier(token, productionId)
      .then((data) => {
        if (data.error) {
          this.setState({ supplyerror: data.error });
        } else {
          this.setState({ supplier: data.suppliers });
        }
      })
      .catch((err) => {
        console.log(err);
      });

    getActivity(token, productionId)
      .then((data) => {
        if (data.error) {
          this.setState({ activityerror: data.error });
        } else {
          this.setState({ activity: data.activities });
        }
      })
      .catch((err) => {
        console.log(err);
      });
  }

  info = () => {
    return this.state.information.length ? (
      this.state.information.map((info, index) => {
        return (
          <h5 key={index} className="list-group-item mr-5">
            {info.name} : {info.description}
          </h5>
        );
      })
    ) : (
      <li className="list-group-item">No Informations</li>
    );
  };
  sup = () => {
    return this.state.supplier.length ? (
      this.state.supplier.map((data, index) => {
        return (
          <h5 key={index} className="list-group-item mr-5">
            {data.name} : {data.description}
          </h5>
        );
      })
    ) : (
      <li className="list-group-item">No Suppliers</li>
    );
  };

  act = () => {
    return this.state.activity.length ? (
      this.state.activity.map((data, index) => {
        return (
          <h5 key={index} className="list-group-item mr-5">
            {data.type} : {data.name}
          </h5>
        );
      })
    ) : (
      <li className="list-group-item">No Activity</li>
    );
  };

  handleChange = (e) => {

    this.setState({ [e.target.name]: e.target.value })

  }

  handleChange1 = (e) => {

    this.setState({ [e.target.name]: e.target.value })

  }

  handleSubmit = (e) => {
    e.preventDefault();
    if(this.state.name==='' || this.state.description==='')
    {   

        NotificationManager.error("Please Fill up Required Field . Please check Name and Description Field");
        return false;
    }

    // let data = { formData: this.state, userData: localStorage.getItem('user') }
    // axios.defaults.headers.common["Authorization"] = localStorage.getItem('token');
    // axios.post("http://localhost:9000/api/task", data).then(res => {
    //     if(res.data.success) NotificationManager.success(res.data.msg);
    // }).catch(error => {
    //     if(error.response.status && error.response.status===400)
    //     NotificationManager.error("Bad Request");
    //     else NotificationManager.error("Something Went Wrong");
    //     this.setState({ errors: error })
    // });
}

handleSubmit1 = (e) => {
  e.preventDefault();
  if(this.state.name1==='' || this.state.description1==='')
  {   
      console.log(this.state.description1)
      NotificationManager.error("Please Fill up Required Field . Please check Supplier and Description Field");
      return false;
  }

  let data = { name: this.state.name1   , description: this.state.description1 }
  a=[data]
  console.log(a);
  // axios.defaults.headers.common["Authorization"] = localStorage.getItem('token');
  // axios.post("http://localhost:9000/api/task", data).then(res => {
  //     if(res.data.success) NotificationManager.success(res.data.msg);
  // }).catch(error => {
  //     if(error.response.status && error.response.status===400)
  //     NotificationManager.error("Bad Request");
  //     else NotificationManager.error("Something Went Wrong");
  //     this.setState({ errors: error })
  // });
}

ButInfo = () => {
    return (
      <Popup
        trigger={<button className="btn btn-sm btn-success ml-5">Add</button>}
        modal
        position="center center"
        closeOnDocumentClick
      >
        <div className="container-fluid">
        <form onSubmit={this.handleSubmit} onChange={this.handleChange} >
            <div className="form-group">
              <p className="text-dark">Enter Information</p>
              <textarea name="name"  id="name" className="form-control"></textarea>

              <p className="text-dark mt-2">Enter Description</p>
              <textarea name="description"  id="description" className="form-control"></textarea>

              <button type="submit" className="btn btn-info my-3">Submit</button>
              <div className="bg-danger">     
              <NotificationContainer/>
              </div>
            </div>
          </form>
        </div>
      </Popup>
    );
  };


  ButSupply = () => {
    return (
      <Popup
        trigger={<button className="btn btn-sm btn-success ml-5">Add</button>}
        modal
        position="center center"
        closeOnDocumentClick
      >
        <div className="container-fluid">
        <form onSubmit={this.handleSubmit1} onChange={this.handleChange1} >
            <div className="form-group">
              <p className="text-dark">Enter Supplier</p>
              <textarea name="name1"  id="name1" className="form-control"></textarea>

              <p className="text-dark mt-2">Enter Description</p>
              <textarea name="description1"  id="description1" className="form-control"></textarea>

              <button type="submit" className="btn btn-info my-3">Submit</button>
              <div className="bg-danger">     
              <NotificationContainer/>
              </div>
            </div>
          </form>
        </div>
      </Popup>
    );
  };




  render() {
    return (
      <Base title="" description="" className="container bg-info p-4">
        <div className="row mb-5">
          <div className="col-12">
            <div className="card mb-4">
              <h4 className="card-header bg-dark text-white text-center">
                Information Flow
                {this.ButInfo()}
              </h4>

              <ul className="list-group">{this.info()}</ul>
            </div>
          </div>
        </div>

        <div className="row mb-5">
          <div className="col-12">
            <div className="card mb-4">
              <h4 className="card-header bg-dark text-white text-center">
                Server Flow
                {this.ButSupply()}

              </h4>

              <ul className="list-group">{this.sup()}</ul>
            </div>
          </div>
        </div>

        <div className="row mb-5">
          <div className="col-12">
            <div className="card mb-4">
              <h4 className="card-header bg-dark text-white text-center">
                Activity Flow
                <Link className="btn btn-sm btn-success ml-5">Add</Link>
              </h4>

              <ul className="list-group">{this.act()}</ul>
            </div>
          </div>
        </div>
      </Base>
    );
  }
}
