import React, { useState } from 'react'
import Base from '../core/Base';
import { useEffect } from 'react';
import { getaCategory } from './helper/adminapicall';
import { Link } from 'react-router-dom';

const UpdateCategory=({match})=>{

    const [category, setcategory] = useState({
        name:"",
        error:"",
        formData:""
    })

    const {name,error,formData}=category;
      const preload=categoryId=>{

        getaCategory(categoryId).then(data=>{
            if(data.error){
                setcategory({...category,error:data.error})            }
            else{
                setcategory({...category,name:data.name,formData:new FormData()});
            }
        })
      }

      useEffect(() => {
          preload(match.params.categoryId);
      }, []);

      const handleChange=name=event=>{
          const value=event.target.value;
          formData.set(name,value)
          setcategory({...category,[name]:value})
      }

      const onSubmit=()=>{
        //
    }
    const goBack = () => (
        <div className="mt-5">
          <Link className="btn btn-sm btn-success mb-3" to="/admin/dashboard">
            Admin Home
          </Link>
        </div>
      );


      const myCategoryForm = () => (
        <form>
          <div className="form-group">
            <p className="lead">Enter the category</p>
            <input
              type="text"
              className="form-control my-3"
              onChange={handleChange}
              value={name}
              autoFocus
              required
              placeholder="For Ex. Summer"
            />
            <button onClick={onSubmit} className="btn btn-outline-info">
              Update Category
            </button>
          </div>
        </form>
      );




return (
    <Base
    title="Update  category here.!"
    description=""
    className="container bg-info p-4"
  >
    <div className="row bg-white rounded">
      <div className="col-md-8 offset-md-2">

        {myCategoryForm()}
        {goBack()}
      </div>
    </div>
      </Base>
);
};




export default UpdateCategory;
