import React, { useState, useEffect } from "react";
import Base from "../core/Base";
import { Link } from "react-router-dom";
import { isAutheticated } from "../auth/helper";
import { getCompany, getProducts ,deleteCategory} from "./helper/adminapicall";

const ManageCompany = () => {
  const [companies, setCompanies] = useState([]);

  const tokrole=JSON.parse(localStorage.getItem("jwt"));
  const token=tokrole.access_token;

  const preload = () => {
    getCompany().then(data => {
      if (data.error) {
        console.log(data.error);
      } else {
        setCompanies(data);
      }
    });
  };

  useEffect(() => {
    preload();
  }, []);

  const deleteThisCategory = categoryId => {
    deleteCategory(categoryId, token).then(data => {
      if (data.error) {
        console.log(data.error);
      } else {
        preload();
      }
    });
  };

  return (
    <Base title="Welcome admin" description="Manage products here">
      <Link className="btn btn-info" to={`/admin/dashboard`}>
        <span className="">Admin Home</span>
      </Link>
      <h2 className="mb-4">Total {companies.length} Companies:</h2>

      <div className="row">
        <div className="col-12">
          {companies.map((company, index) => {
            return (

              <div key={index} className="row text-center mb-2 ">
              <div  className="col-4">
               <h3 className="text-white text-left">{index+1}.{company.name}  {company.address}</h3>
              </div>
              <div className="col-4">
                <Link
                  className="btn btn-outline-success"
                  to={`/admin/category/update/${company._id}`}
                >
                  <span className="">Update</span>
                </Link>
              </div>
              <div className="col-4">
                <button onClick={() => {deleteThisCategory(company._id)}} className="btn btn-outline-danger">
                  Delete
                </button>
              </div>
          </div>

          )

          })}
          </div>
      </div>

    </Base>
  );
};

export default ManageCompany;
