import React, { useState } from "react";
import Base from "../core/Base";
import { isAutheticated } from "../auth/helper";
import { Link } from "react-router-dom";
import { createCompany } from "./helper/adminapicall";

const AddCompany = () => {
  const [values, setValues] = useState({
    name: "",
    description: "",
    details: "",
    contact:"",
    error: false,
    success: false
  });

  const { name,description,details,contact, error, success } = values;

  const { user, token } = isAutheticated();

  const goBack = () => (
    <div className="mt-5">
      <Link className="btn btn-sm btn-success mb-3" to="/admin/dashboard">
        Admin Home
      </Link>
    </div>
  );


  const handleChange = name => event => {
    setValues({ ...values, error: "", [name]: event.target.value });
  };

  const onSubmit = event => {
    event.preventDefault();
    setValues({ ...values, error: "",success:false});


    //backend request fired
    createCompany(user._id, token, { name }).then(data => {
      if (data.error) {
        setValues({ ...values, error: true});
      } else {
        setValues({ ...values, error: "",success:false,name:""});;
      }
    });
  };

  const successMessage = () => {
    if (success) {
      return <h4 className="text-success">Company added successfully</h4>;
    }
  };

  const warningMessage = () => {
    if (error) {
      return <h4 className="text-success">Failed to add company</h4>;
    }
  };

  const myCompanyForm = () => (
    <form>
      <div className="form-group">
        <p className="lead">Enter Company Name</p>
        <input
          type="text"
          className="form-control my-3"
          onChange={handleChange("name")}
          value={name}
          autoFocus
          required
          placeholder=""
        />
        <p className="lead">Enter Company Description</p>
        <input
          type="text"
          className="form-control my-3"
          onChange={handleChange("description")}
          value={description}
          autoFocus
          required
          placeholder=""
        />

        <p className="lead">Enter Company Details</p>
        <input
          type="text"
          className="form-control my-3"
          onChange={handleChange("details")}
          value={details}
          autoFocus
          required
          placeholder=""
        />

        <p className="lead">Enter Company Contact Number</p>
        <input
          type="text"
          className="form-control my-3"
          onChange={handleChange("contact")}
          value={contact}
          autoFocus
          required
          placeholder=""
        />
        <button onClick={onSubmit} className="btn btn-outline-info">
          Add Company
        </button>
      </div>
    </form>
  );

  return (
    <Base
      title="You Can Add Company Here"
      description="Enter Company Details"
      className="container bg-info p-4"
    >
      <div className="row bg-white rounded">
        <div className="col-md-8 offset-md-2">
          {successMessage()}
          {warningMessage()}
          {myCompanyForm()}
          {goBack()}
        </div>
      </div>
    </Base>
  );
};

export default AddCompany;
