import React, { useState } from 'react'
import Base from '../core/Base';
import { useEffect } from 'react';
import { getaCategory, updateCategory } from './helper/adminapicall';
import { Link } from 'react-router-dom';
import { isAutheticated } from '../auth/helper';


const UpdateCategory=({match})=>{

    const {user,token}=isAutheticated();

    const [name, setname] = useState();
    const [error, seterror] = useState();
    const [success, setsuccess] = useState();

      const preload=categoryId=>{

        getaCategory(categoryId).then(data=>{
            if(data.error){
              seterror(data.error)
            }
            else{
                setname(data.name);
            }
        })
      }

      useEffect(() => {
          preload(match.params.categoryId);
      }, []);

      const handleChange=name=>event=>{
          const value=event.target.value;

          setname(value)
      }

      const onSubmit=(event)=>{
        event.preventDefault();
        updateCategory(match.params.categoryId, user._id, token , name).then(data=>{
            if(data.error){
                seterror(data.error)
            }
            else{
               setname(data.name);
               setsuccess(true)
            }
        })


    }
    const goBack = () => (
        <div className="mt-5">
          <Link className="btn btn-sm btn-success mb-3" to="/admin/dashboard">
            Admin Home
          </Link>
        </div>
      );

      const successMessage = () => {
        return{
        if(success){
        <div
          className="alert alert-success mt-3"

        >
          <h4>{name} updated successfully</h4>
        </div>
      }}};

      const warningMessage = () => {
        return{
        if(error){
        <div
          className="alert alert-success mt-3"

        >
          <h4>{name} updatation failed</h4>
        </div>
      }}};


      const myCategoryForm = () => (
        <form>
          <div className="form-group">
            <p className="lead">Enter the category</p>
            <input
              type="text"
              className="form-control my-3"
              onChange={handleChange("name")}
              value={name}
              autoFocus
              required
              placeholder="For Ex. Summer"
            />
            <button onClick={onSubmit} className="btn btn-outline-info">
              Update Category
            </button>
          </div>
        </form>
      );




return (
    <Base
    title="Update  category here.!"
    description=""
    className="container bg-info p-4"
  >
    <div className="row bg-white rounded">
      <div className="col-md-8 offset-md-2">

        {myCategoryForm()}
        {successMessage()}
        {warningMessage()}
        {goBack()}
      </div>
    </div>
      </Base>
);
};




export default UpdateCategory;
