import React from 'react'
import { getCategories } from './helper/adminapicall'
import { useState } from 'react'
import { isAutheticated } from '../auth/helper';
import Base from '../core/Base';
import { Link } from 'react-router-dom';
import { useEffect } from 'react';
export default function ManageCategories() {

const {user, token} = isAutheticated();


const [category, setcategory] = useState([]);

const preload=()=>{
  getCategories().then(data=>{
    if(data.error){
      console.log(data.err)
    }
    else{
      setcategory(data);
    }
  })
}

useEffect(() => {
  preload()
}, [])



  return (
    <Base title="Welcome admin" description="Update Categories here!">
    {category.map((cat,index)=>{
      return(
    <div className="row">

      <div key={index} value={cat.value} className="col-4">
    <h3>{cat.name}</h3>
    </div>


        <div className="col-4">
        <Link to={`/categ`} className="btn btn-success mb-4">Update</Link>
        </div>

        <div className="col-4">
        <button className="btn btn-danger mt-6">Delete</button>
        </div>


    </div>

    )})}

    </Base>

  )
}
