import React,{useState,useEffect} from 'react'
import Base from '../core/Base'
import { Link } from 'react-router-dom'
import { getCategories } from './helper/adminapicall'
import { isAutheticated } from '../auth/helper'
import { is } from '@babel/types'
 const  AddProduct=()=> {
  const [values, setvalues] = useState({
      name:"",
      description:"",
      price:"",
      stock:"",
      photo:"",
      categories:[],
      category:"",
      loading:false,
      error:"",
      createdProduct:"",
      detaRedirect:"",
      formData:""
  })

    const {name,price,stock,description,categories,category,loading,error,createdProduct,getaRedirect,formData}=values;


    const preload=()=>{
        getCategories().then(data=>{
            if(data.error){
                setvalues({...values,error:data.error})
            }
            else{
                setvalues({...values, categories:data,formData:new FormData()})
                console.log("cate:",categories);
            }
        })
    }
    useEffect(() => {
       preload(); 
    }, [])

    const {user,token} = isAutheticated();
    const onSubmit=()=>{
        //
    }

    const handleChange =name=>event=>{
        const value = name==="photo"? event.target.file[0]:event.target.value;
        formData.set(name,value)
        setvalues({...values,[name]:value})

    }
    const createProductForm = () => (
        <form >
          <span>Post photo</span>
          <div className="form-group">
            <label className="btn btn-block btn-success">
              <input
                onChange={handleChange("photo")}
                type="file"
                name="photo"
                accept="image"
                placeholder="choose a file"
              />
            </label>
          </div>
          <div className="form-group">
            <input
              onChange={handleChange("name")}
              name="photo"
              className="form-control"
              placeholder="Name"
              value={name}
            />
          </div>
          <div className="form-group">
            <textarea
              onChange={handleChange("description")}
              name="photo"
              className="form-control"
              placeholder="Description"
              value={description}
            />
          </div>
          <div className="form-group">
            <input
              onChange={handleChange("price")}
              type="number"
              className="form-control"
              placeholder="Price"
              value={price}
            />
          </div>
          <div className="form-group">
            <select
              onChange={handleChange("category")}
              className="form-control"
              placeholder="Category"
            >
              <option>Select</option>
              {categories && 
              categories.map((cate,index)=>(
              <option key={index} value={cate._id}>{cate.name}</option>

              ))

              }
            </select>
          </div>
          <div className="form-group">
            <input
              onChange={handleChange("quantity")}
              type="number"
              className="form-control"
              placeholder="Quantity"
              value={stock}
            />
          </div>

          <button type="submit" onClick={onSubmit} className="btn mb-3 btn-outline-success">
            Create Product
          </button>
        </form>
      );

    return (
    <Base title="Add a Product here!" description="Welcome to Prodcut Creation Section" className="container bg-info p-4">
    <Link to="/admin/dashboard" className="btn btn-md mb-3 btn-dark ">Admin Home</Link>
    <div className="row bg-dark text-white rounded">
    <div className="col-md-8 offest-md-2">
    {createProductForm()}
    </div>
    </div>
    </Base>


    )

}
export default AddProduct;
