import React, { useState, useEffect } from "react";
import Base from "../core/Base";
import { Link } from "react-router-dom";
import { getProduction } from "../admin/helper/adminapicall";


const CompanyDashBoard = ({match}) => {

    const tokrole=JSON.parse(localStorage.getItem("jwt"));
    const token=tokrole.access_token;

  const [values, setValues] = useState({
    name: "",
    production: "",
    no:0

  });

  const { name, production ,no} = values;


  const preload=(companyId)=>{
  getProduction(token,companyId).then(response=>{
    setValues({ ...values, name: response.name, production:response.productions});
    if(production.length){
      console.log(production)
    }
    else{
      setValues({...values,no:"No productions"})
    }


    }).catch(err=>{console.log(err)})
   };



  useEffect(() => {
    preload(match.params.companyId);
  }, []);






  const companyLeftSide = () => {
    return (
      <div className="card">
        <h4 className="card-header bg-dark text-white">Navigation</h4>
        <ul className="list-group">
          <li className="list-group-item">
            <Link to="/admin/create/company" className="nav-link text-info">
              Add Production
            </Link>
          </li>

        </ul>
      </div>
    );
  };

  const companyRightSide =() => {

    return   (


      <div className="card mb-4">
        <h4 className="card-header">Company Information</h4>
        <ul className="list-group">
          <li className="list-group-item">
            <span className="badge badge-info mr-2">Company Name:</span>{no}
          </li>
          <li className="list-group-item">
            <span className="badge badge-info mr-2">Productions:</span>{name}
          </li>

          <li className="list-group-item">
            <span className="badge badge-danger">Company Area</span>
          </li>
        </ul>
      </div>
    );
  };
  return (
    <Base
      title="Welcome to Company area"
      description="Manage all of your Company Data here"
      className="container bg-info p-4"
    >
      <div className="row">
        <div className="col-3">{companyLeftSide()}</div>
        <div className="col-9">{companyRightSide()}</div>
      </div>
    </Base>
  );
};

export default CompanyDashBoard;
