import React, { useState } from 'react'
import Base from '../core/Base';
import { useEffect } from 'react';
import { getaCategory, updateCategory } from './helper/adminapicall';
import { Link } from 'react-router-dom';
import { isAutheticated } from '../auth/helper';


const UpdateCategory=({match})=>{

    const {user,token}=isAutheticated();

    const [category, setcategory] = useState({
        name:"",
        error:"",
        loading:"false",
        createdCategory:"",
        formData:""
    })

    const {name,error,formData, createdCategory}=category;
      const preload=categoryId=>{

        getaCategory(categoryId).then(data=>{
            if(data.error){
                setcategory({...category,error:data.error})            }
            else{
                setcategory({...category,name:data.name,formData:new FormData()});
            }
        })
      }

      useEffect(() => {
          preload(match.params.categoryId);
      }, []);

      const handleChange=name=>event=>{
          const value=event.target.value;
          formData.set(name,value)
          setcategory({...category,[name]:value})
      }

      const onSubmit=(event)=>{
        event.preventDefault();
        setcategory({...category,error:"",loading:true})
        updateCategory(match.params.categoryId, user._id, token , formData).then(data=>{
            if(data.error){
                setcategory({...category,error:data.error})
            }
            else{
                setcategory({...category,
                name:"",
            loading:false,
            createdCategory:data.name
            })
            }
        })


    }
    const goBack = () => (
        <div className="mt-5">
          <Link className="btn btn-sm btn-success mb-3" to="/admin/dashboard">
            Admin Home
          </Link>
        </div>
      );

      const successMessage = () => (
        <div
          className="alert alert-success mt-3"
          style={{ display: createdCategory ? "" : "none" }}
        >
          <h4>{createdCategory} updated successfully</h4>
        </div>
      );

      const warningMessage = () => (
        <div
          className="alert alert-danger mt-3"
          style={{ display: createdCategory ? "none" : "" }}
        >
          <h4>{createdCategory} updation failed</h4>
        </div>
      );



      const myCategoryForm = () => (
        <form>
          <div className="form-group">
            <p className="lead">Enter the category</p>
            <input
              type="text"
              className="form-control my-3"
              onChange={handleChange("name")}
              value={name}
              autoFocus
              required
              placeholder="For Ex. Summer"
            />
            <button onClick={onSubmit} className="btn btn-outline-info">
              Update Category
            </button>
          </div>
        </form>
      );




return (
    <Base
    title="Update  category here.!"
    description=""
    className="container bg-info p-4"
  >
    <div className="row bg-white rounded">
      <div className="col-md-8 offset-md-2">

        {myCategoryForm()}
        {goBack()}
      </div>
    </div>
      </Base>
);
};




export default UpdateCategory;
