import React, { useState, useEffect } from "react";
import Base from "../core/Base";
import { Link } from "react-router-dom";
import { isAutheticated } from "../auth/helper";
import { getCompany, getProducts ,deleteCategory, deleteCompany} from "./helper/adminapicall";

const ManageCompany = () => {
  const [companies, setCompanies] = useState([]);


  const tokrole=JSON.parse(localStorage.getItem("jwt"));
  const token=tokrole.access_token;
  const preload = () => {
    getCompany(token).then(data => {
      if (data.error) {
        console.log(data.error);
      } else {
        setCompanies(data);
      }
    });
  };

  useEffect(() => {
    preload();
  }, []);

  const deleteThisCompany = (token,companyId) => {


    deleteCompany(token,companyId).then(data => {
      if (data.error) {


        console.log(data.error);
      } else {
        preload();
      }
    });
  };

  return (
    <Base title="Welcome Admin" description="Manage companies here" className="container bg-secondary p-4">
      <Link className="btn btn-info" to={`/admin/dashboard`}>
        <span className="">Admin Home</span>
      </Link>
      <h2 className="my-4">Total {companies.length} Companies:</h2>

      <div className="row">
        <div className="col-12">
          {companies.map((company, index) => {
            return (

              <div key={index} className="row text-center mb-2 ">
              <div  className="col-4">
               <h3 className="text-dark text-left">{index+1}.{company.name}  {company.address}  </h3>
              </div>
              <div className="col-4">
                <Link
                  className="btn btn-success"
                  to={`/admin/company/update/${company._id}`}
                ><i class="fa fa-pencil-square-o fa-5x"></i>

                </Link>
              </div>
              <div className="col-4">
                <button onClick={() => {deleteThisCompany(token,company._id)}} className="btn btn-danger">
                  Delete
                </button>
              </div>
          </div>

          )

          })}
          </div>
      </div>

    </Base>
  );
};

export default ManageCompany;
