import React from 'react'
import { getCategories, deleteCategory } from './helper/adminapicall'
import { useState } from 'react'
import { isAutheticated } from '../auth/helper';
import Base from '../core/Base';
import { Link } from 'react-router-dom';
import { useEffect } from 'react';
export default function ManageCategories() {

const {user, token} = isAutheticated();


const [category, setcategory] = useState([]);
const deleteaCategory=categoryId=>{
  deleteCategory(categoryId,user._id,token).then(data=>{
    if(data.error){
      console.log(data.error)
      console.log(category._id)

    }
    else{
      preload();
    }
  })
}

const preload=()=>{
  getCategories().then(data=>{
    if(data.error){
      console.log(data.err)
    }
    else{
      setcategory(data);
    }
  })
}

useEffect(() => {
  preload()
}, [])



  return (
    <Base title="Welcome admin" description="Update Categories here!">
    {category.map((cat,index)=>{
      return(
    <div className="row">

      <div key={index} value={cat.value} className="col-4">
    <h3>{cat.name}</h3>
    </div>


        <div className="col-4">
        <Link  className="btn btn-success mb-4">Update</Link>
        </div>

        <div className="col-4">
        <button onClick={()=>{
          deleteaCategory(category._id);
        }} className="btn btn-danger mt-6">Delete</button>
        </div>
        <p className="text-danger" >hii {cat._id}</p>


    </div>

    )})}

    </Base>

  )
}
